select c._id as Id, c.titulo as Titulo 
from curto c, longo l 
where l.conteudo like '%direito%' and c._id = l._id_curto;

select  c.titulo as Titulo, c.conteudo as Curto, l.conteudo as Longo 
from curto c inner join longo l
on c._id = l._id_curto 
where l.conteudo like '%direito%' ;

SELECT c.conteudo as curto, l.conteudo as Longo, r.referencia as Refercia
FROM curto c, longo l, referencia r
WHERE c._id = l._id_curto and l._id = r._id_longo and c._id = 2;

SELECT c._id as ID, c.conteudo as Curto, l.conteudo as Longo, r.referencia as Referencias
FROM curto c, longo l, referencia r
WHERE c._id = l._id_curto and r._id_longo = l._id and c._id = 7;
